import React, { useMemo} from 'react'
import { useTable, useSortBy } from 'react-table'
import MOCK_DATA from './MOCK_DATA.json'
import { COLUMNS } from './columns'
import SortAZ from '../SVG/SortAZ';
import SortZA from '../SVG/SortZA';
export const BasicTable = () => {
    const columns = useMemo(() => COLUMNS, []);
    const data = useMemo(() => MOCK_DATA, []);

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable({
        columns,
        data,
      },
    useSortBy);

    return (
      <div className="flex flex-col min-h-screen py-16 bg-gray-300">
        <div className="bg-white rounded-lg mx-auto w-4/5 h-4/5 p-8 shadow-lg">
          <div className="w-full mx-auto">
            <table
              {...getTableProps}
              className="w-full table-auto border-collapse rounded-lg overflow-hidden"
            >
              <thead className="text-black">
                {headerGroups.map((headerGroup, index) => (
                  <tr key={index} {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column, index) => (
                      <th
                        key={index}
                        {...column.getHeaderProps(column.getSortByToggleProps())}
                        className={`justify-between px-4 py-2 font-bold bg-yellow-500 text-left ${
                          index === 0 ? 'rounded-l-lg' : ''
                        } ${index === headerGroup.headers.length - 1 ? 'rounded-r-lg' : ''}`}
                      >
                        {column.render('Header')}
                        
                        {column.isSorted ? (
  column.isSortedDesc ? <SortAZ /> : <SortZA />
) : (
  <>
    {' '}
    <SortZA className="visible hover:invisible" />
  </>
)}

                        
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps} className="text-gray-800">
                {rows.map((row, index) => {
                  prepareRow(row);
                  return (
                    <tr
                      key={index}
                      {...row.getRowProps()}
                      className={`${
                        index % 2 === 1 ? 'bg-gray-200' : 'bg-white'
                      } hover:bg-yellow-500 transition-colors duration-300`}
                    >
                      {row.cells.map((cell, index) => {
                        return (
                          <td
                            key={index}
                            {...cell.getCellProps()}
                            className={`px-4 py-2 text-left ${
                              index === 0 ? 'rounded-l-lg' : ''
                            } ${index === row.cells.length - 1 ? 'rounded-r-lg' : ''}`}
                          >
                            {cell.render('Cell')}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  };
