import React, { useMemo} from 'react'
import { useTable } from 'react-table'
import MOCK_DATA from './MOCK_DATA.json'
import { COLUMNS } from './columns'
export const BasicTable = () => {
    const columns = useMemo(() => COLUMNS, []);
    const data = useMemo(() => MOCK_DATA, []);
    const tabLeInstance = useTable({
      columns,
      data
    });
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tabLeInstance;
  
    return (
      <div className="py-4 px-6 bg-gray-300">
        <table {...getTableProps} className="w-full border border-yellow-500 border-collapse">
          <thead className="text-gray-800">
            {headerGroups.map((headerGroup, index) => (
              <tr key={index} {...headerGroup.getHeaderGroupProps()} className="bg-yellow-500">
                {headerGroup.headers.map((column, index) => (
                  <th key={index} {...column.getHeaderProps()} className="px-4 py-2 font-extrabold">
                    {column.render('Header')}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps} className="text-gray-800">
            {rows.map((row, index) => {
              prepareRow(row);
              return (
                <tr key={index} {...row.getRowProps()} className={ 'bg-white'}>
                  {row.cells.map((cell, index) => {
                    return <td key={index} {...cell.getCellProps()} className="px-4 py-2">{cell.render('Cell')}</td>;
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };
