import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom"
import Plus from '../SVG/Plus';
import Minus from '../SVG/Minus';

export const Orders = ({ basketData, setBasketData, loginData}) => {
  const handleQuantityChange = (index, value) => {
    if (value === 0) {
      setBasketData((prevData) =>
        prevData.filter((_, i) => i !== index)
      );
    } else {
      setBasketData((prevData) => {
        const updatedData = [...prevData];
        updatedData[index].clickCount = value;
        return updatedData;
      });
    }
  };

  const decrementQuantity = (index) => {
    const item = basketData[index];
    if (item.clickCount > 0) {
      handleQuantityChange(index, item.clickCount - 1);
    }
  };

  const incrementQuantity = (index) => {
    const item = basketData[index];
    handleQuantityChange(index, item.clickCount + 1);
  };

  const getTotalPrice = () => {
    return basketData.reduce((total, item) => total + item.price * item.clickCount, 0).toFixed(2);
  };

  const getProductNoun = (count) => {
    const lastDigit = count % 10;
    const lastTwoDigits = count % 100;

    if (lastDigit === 1 && lastTwoDigits !== 11) {
      return 'товар';
    } else if ([2, 3, 4].includes(lastDigit) && ![12, 13, 14].includes(lastTwoDigits)) {
      return 'товара';
    } else {
      return 'товаров';
    }
  };
  const handleSubmitOrder = async () => {
    try {
      const orderData = {
        items: basketData.map((item) => ({
          productId: item.id,
          quantity: item.clickCount,
        })),
        clientCode: loginData['id'], 
        paymentStatus: 'Ожидает подтверждения', 
        deliveryCode: 1, 
        deliveryAdress: 'Ярославль',
        deliveryStatus: 'Самовывоз'
      };
  
      const response = await fetch('/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderData),
      });
  
      if (response.ok) {
        const data = await response.json();
        console.log('Заказ успешно создан:', data);
      } else {
        console.error('Ошибка при создании заказа:', response.status);
      }
    } catch (error) {
      console.error('Ошибка при создании заказа:', error);
    }
  };

  return (
    <div className="flex flex-row py-8 bg-gray-300 min-h-screen">
      
      {basketData.length === 0 ? (
        <div className="max-h-56 w-[1000px] bg-white rounded-lg p-8 shadow-lg m-10">
          <h1 className="text-2xl font-bold mb-4 my-5">Корзина пуста</h1>
          <div className="text-xl my-10 ">
            <Link to="/" className="underline text-yellow-500">
              Нажмите здесь
            </Link>
            , чтобы продолжить покупки
          </div>
        </div>
      ) : (
        <div className="w-full flex">
          <div className="w-full flex flex-col">
          <div class="flex-grow bg-white rounded-lg p-8 shadow-lg m-10 overflow-auto">
      <legend className='text-2xl font-bold mb-4'>Ваши данные</legend>
      <div class="space-y-4">
        <div class="flex justify-between items-center">
          <label for="client_id" class="font-bold">Номер клиента</label>
          <input type="text" id="client_id" name="client_id" value={loginData['id']} readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        <div class="flex justify-between items-center">
          <label for="firm" class="font-bold">Наименование организации</label>
          <input type="text" id="firm" name="firm" value={loginData['company']} readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
          {loginData['companyForm'] !== 'Физлицо'  && (
            <div class="flex justify-between items-center">
              <label for="main_organization" class="font-bold">Основная организация</label>
              <input type="text" id="main_organization" name="main_organization" value={loginData['companyMain']} readonly class="bg-gray-200 rounded px-4 py-2" />
              </div>
          )}
        <div class="flex justify-between items-center">
          <label for="inn" class="font-bold">ИНН</label>
          <input type="text" id="inn" name="inn" value={loginData['inn']} readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        {loginData['companyForm'] !== 'Физлицо'  && (
        <div class="flex justify-between items-center">
          <label for="kpp" class="font-bold">КПП</label>
          <input type="text" id="kpp" name="kpp" value="1234567890" readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        )}
        <div class="flex justify-between items-center">
          <label for="street" class="font-bold">Улица</label>
          <input type="text" id="street" name="street" value="квартал 13, участок 3, литер Д Ломоносовский р-н" readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        <div class="flex justify-between items-center">
          <label for="adress" class="font-bold">Адрес</label>
          <input type="text" id="adress" name="adress" value="RU - 188508 Малое Карлино д" readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        {loginData['region'] !== ''  && (
        <div class="flex justify-between items-center">
          <label for="region" class="font-bold">Регион</label>
          <input type="text" id="region" name="region" value="Московская область" readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        )}
                {loginData['companyForm'] !== 'Физлицо'  && (
          <div class="flex justify-between items-center">
          <label for="companyFormUR" class="font-bold">Юр. / Физ. лицо</label>
          <input type="text" id="companyFormUR" name="companyFormUR" value={loginData['companyForm']} readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
          )}
          {loginData['companyForm'] !== 'Юрлицо'  && (
         <div class="flex justify-between items-center">
          <label for="companyFormIP" class="font-bold">Юр. / Физ. лицо</label>
          <input type="text" id="companyFormIP" name="companyFormIP" value={loginData['companyForm']} readonly class="bg-gray-200 rounded px-4 py-2" />
        </div>
        )}
        <div class="flex justify-between items-center">
          <label for="client_surname" class="font-bold">Фамилия</label>
          <input type="text" id="client_surname" name="client_surname" value={loginData['surname']} class="bg-gray-200 rounded px-4 py-2 readonly" />
        </div>
        <div class="flex justify-between items-center">
          <label for="client_name" class="font-bold">Имя</label>
          <input type="text" id="client_name" name="client_name" value={loginData['name']} class="bg-gray-200 rounded px-4 py-2 readonly" />
        </div>
        {loginData['othce'] != null && (
        <div class="flex justify-between items-center">
          <label for="client_patronymic" class="font-bold">Отчество</label>
          <input type="text" id="client_patronymic" name="client_patronymic" value={loginData['othce']} class="bg-gray-200 rounded px-4 py-2 readonly" />
        </div>
        )}
        <div class="flex justify-between items-center">
          <label for="phone_number" class="font-bold">Номер телефона</label>
          <input type="text" id="phone_number" name="phone_number" value={loginData['phone']} class="bg-gray-200 rounded px-4 py-2 readonly" />
        </div>
        <div class="flex justify-between items-center">
          <label for="email" class="font-bold">E-mail</label>
          <input type="email" id="email" name="email" value={loginData['email']} class="bg-gray-200 rounded px-4 py-2 readonly" />
        </div>
      </div>
</div>
<div className="flex-grow bg-white rounded-lg p-8 shadow-lg m-10 overflow-auto">
<div class="flex-grow bg-white rounded-lg p-8 shadow-lg m-10 overflow-auto">
        <legend>Вид доставки</legend>
        <div class="space-y-2">
          <div>
            <input id="pickup" name="del_type" type="radio" value="pickup" required class="mr-2" />
            <label for="pickup">Самовывоз</label>
          </div>
          <div>
            <input id="delivery_by_company" name="del_type" type="radio" value="delivery_by_company" required class="mr-2" />
            <label for="delivery_by_company">Доставка транспортной компанией</label>
          </div>
        </div>

    <fieldset class="mt-4">
      <legend>Удобное время получения товара</legend>
      <div class="flex justify-between items-center">
        <div class="flex items-center">
          <label for="from_time" class="font-bold mr-2">Не раньше</label>
          <input type="time" id="from_time" name="from_time" required class="bg-gray-200 rounded px-4 py-2" />
        </div>
        <div class="flex items-center">
          <label for="to_time" class="font-bold mr-2">Не позднее</label>
          <input type="time" id="to_time" name="to_time" required class="bg-gray-200 rounded px-4 py-2" />
        </div>
      </div>
    </fieldset>
        </div>
  </div>
          <div className="flex-grow bg-white rounded-lg p-8 shadow-lg m-10 overflow-auto">
            <h1 className="text-2xl font-bold mb-4">{basketData.length} {getProductNoun(basketData.length)} в корзине</h1>
            <table className="table-auto w-full border-collapse">
              <tbody>
                {basketData.map((item, index) => (
                  <tr key={index} className="border-b">
                    <td className="px-4 py-2">
                      <span className="block font-bold">{item.name}</span>
                      <span className="block text-gray-500">Артикуль: {item.id}</span>
                    </td>
                    <td className="px-4 py-2 flex items-center justify-center">
                      <div className="items-center mt-2 max-w-32 bg-gray-300 rounded-full flex justify-center">
                        <button
                          onClick={() => decrementQuantity(index)}
                          className="text-gray-800 font-bold py-1 px-2">
                          <Minus/>
                        </button>
                        <input
                          type="number"
                          value={item.clickCount}
                          min="0"
                          onChange={(e) => { if (e.target.value >= 1) { handleQuantityChange(index, parseInt(e.target.value)) } else { handleQuantityChange(index, 1) } }}
                          className="w-10 px-2 py-1 border-none bg-gray-300 text-gray-800 text-center text-lg"
                        />
                        <button
                          onClick={() => incrementQuantity(index)}
                          className="text-gray-800 font-bold py-1 px-2"
                        >
                          <Plus/>
                        </button>
                      </div>
                    </td>
                    <td className="px-4 py-2 text-black font-bold text-xl">{(item.price * item.clickCount).toFixed(2)} руб.</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          </div>
          {basketData.length > 0 && (
            <div className="w-[500px] max-h-48 bg-white rounded-lg p-8 shadow-lg m-10 flex flex-col justify-between">
              <div className="flex justify-between items-center">
                <p className="text-3xl font-bold">Итого:</p>
                <p className="text-3xl font-bold">{getTotalPrice()} руб.</p>
              </div>
              <button className="bg-yellow-500 w-full h-[50px] text-gray-800 font-bold py-2 px-8 rounded-full text-xl" onClick={handleSubmitOrder}>
                Оформить заказ
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};
