import React, { useState, useEffect, useMemo } from 'react'
import { useTable, useSortBy, useGlobalFilter, useFilters, usePagination, useRowSelect } from 'react-table'
import MOCK_DATA from './MOCK_DATA.json'
import { COLUMNS } from './columns'
import SortAZ from '../SVG/SortAZ';
import SortZA from '../SVG/SortZA';
import { GlobalFilter } from './GlobalFilter';
import { ColumnFilter } from './ColumnFilter';
import PushLeft from '../SVG/PushLeft';
import PushRight from '../SVG/PushRight';
import FirstPage from '../SVG/FirstPage';
import LastPage from '../SVG/LastPage';
import { Checkbox } from './CheckBox';
export const BasicTable = () => {
    const columns = useMemo(() => COLUMNS, []);

    const [data, setData] = useState([]);


    useEffect(() => {
      // Получаем данные из модели 'productInfo'
      fetch('/').then(response => response.json())
          .then(data => setData(data));
  }, []);
    const { getTableProps, getTableBodyProps, headerGroups, page, nextPage, previousPage, canPreviousPage, canNextPage,
       pageOptions, prepareRow, state, setGlobalFilter, gotoPage, pageCount, setPageSize, selectedFlatRows } = useTable({
        columns,
        data,
        initialState: { pageSize: 50 },
      },
      useFilters,
      useGlobalFilter,
    useSortBy,
    usePagination,
    useRowSelect,
    hooks => {
      hooks.visibleColumns.push(columns => [
        ...columns,
        {
          id: 'selection',
          Header: () => null,
          Cell: ({ row }) => (
            <Checkbox
              {...row.getToggleRowSelectedProps()}
              onClick={() => {
                row.toggleRowSelected();
              }}
            />
          )
        }
      ])
    }
    );
  
    
    const { globalFilter, pageIndex, pageSize} = state
    const handlePageChange = () => {
      // Скролл наверх страницы
      window.scrollTo({
        top: 0,
        behavior: 'smooth',
      });
    };

    return (
      <div className="flex flex-row min-h-screen py-8  bg-gray-300">
      <div className="bg-white rounded-lg w-2/12  p-8 shadow-lg w-60 py-2 place-items-start mx-8 absolute">
  <h1 className="py-2 text-gray-800 font-bold">Фильтр</h1>
  <GlobalFilter filter={globalFilter} setFilter={setGlobalFilter}/>
  <ColumnFilter column={headerGroups[0].headers[4]}/>
</div>
        <div className="bg-white rounded-lg mx-auto w-8/12 p-8 shadow-lg ">
        <div className="justify-center bg-white rounded-lg w-full p-2 ">

          <div className="mx-auto ">
          
          <table
  {...getTableProps}
  className="table-auto w-full border-collapse rounded-lg overflow-hidden mx-auto max-w-full"
>
              <thead className="text-black w-full">
                {headerGroups.map((headerGroup, index) => (
                  <tr key={index} {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column, index) => (
                      <th
                        key={index}
                        {...column.getHeaderProps(column.getSortByToggleProps())}
                        className={`justify-between px-4 py-2 font-bold bg-yellow-500 text-left ${
                          index === 0 ? 'rounded-l-lg' : ''
                        } ${index === headerGroup.headers.length - 1 ? 'rounded-r-lg' : ''}`}
                      >
                        {column.render('Header')}
                        {/* <div>{column.canFilter?column.render('Filter'):null}</div> */}
                        {column.isSorted? column.isSortedDesc? <SortAZ />: <SortZA />:''}
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps} className="text-gray-800 w-full">
                {page.map((row, index) => {
                  prepareRow(row);
                  return (
                    <tr
                      key={index}
                      {...row.getRowProps()}
                      className={`${
                        index % 2 === 1 ? 'bg-gray-200' : 'bg-white'
                      } hover:bg-yellow-500 transition-colors duration-300 w-full`}
                    >
                      {row.cells.map((cell, index) => {
                        return (
                          <td
                            key={index}
                            {...cell.getCellProps()}
                            className={`px-4 py-2 text-left w-full  ${
                              index === 0 ? 'rounded-l-lg' : ''
                            } ${index === row.cells.length - 1 ? 'rounded-r-lg' : ''}`}
                          >
                            {cell.render('Cell')}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </tbody>
           
            </table>
            
            <div className='flex flex-col gap-4'>

  <div className='flex justify-between items-center py-2'>
  {/* Select */}
  <select className='w-[200px] border-black border font-bold rounded-lg text-sm rounded-lg block p-1.5 text-color-black'
      value={pageSize}
      onChange={e => setPageSize(Number(e.target.value))}
  >
    {[50, 100, 200].map(pageSize => (
      <option key={pageSize} value={pageSize}>
        {pageSize}
      </option>
    ))}
  </select>

  <div className='flex justify-center gap-2 flex-grow'>
    {/* Кнопки */}
    <button onClick={()=> {gotoPage(0); handlePageChange(); }} ><FirstPage/></button>
    <button onClick={()=> {previousPage(); handlePageChange();}} ><PushLeft/></button>
    <strong >{pageIndex + 1} из {pageOptions.length}</strong>
    <button onClick={()=> {nextPage(); handlePageChange();}} ><PushRight/></button>
    <button onClick={()=> {gotoPage(pageCount-1); handlePageChange();}} ><LastPage/></button>
  </div>
  <input
  className=' w-[200px] font-bold border-solid border border-black rounded-lg block p-1.5 placeholder-black'
  type='number'
  placeholder='Поиск страницы'
  onChange={e => {
    const pageNumber = e.target.value ? Number(e.target.value) - 1 : ''
    gotoPage(pageNumber)
  }}
/>


</div>
<pre>
        <code>
          {JSON.stringify(
            {
              selectedFlatRows: selectedFlatRows.map(row => row.original)
            },
            null,
            2
          )}
        </code>
      </pre>
</div>
</div>
          </div>
        </div>
      </div>
    );
  };
