import React, { useState } from 'react';

export const Checkbox = React.forwardRef(({ onClick, isSelected, rowData, rowClickCounts, setRowClickCounts, ...rest }, ref) => {
  const [quantity, setQuantity] = useState(1);

  const handleClick = (event) => {
    setRowClickCounts(prevCounts => {
      const newCounts = { ...prevCounts };
      newCounts[rowData] = (newCounts[rowData] || 0) + quantity;
      return newCounts;
    });
    onClick(event);
  };

  const clickCount = rowClickCounts[rowData] || 0;

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const incrementQuantity = () => {
    setQuantity(quantity + 1);
  };

  return (
    <>
      <button
        ref={ref}
        onClick={handleClick}
        {...rest}
        className="bg-gray-800 hover:bg-yellow-500 text-white font-bold py-2 px-4 rounded"
      >
        Добавить в корзину
      </button>
      <div className="flex items-center mt-2">
        <button
          onClick={decrementQuantity}
          className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
        >
          -
        </button>
        <input
          type="number"
          value={quantity}
          min="1"
          onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
          className="mx-2 py-2 px-4 rounded-md border border-gray-300 text-center"
        />
        <button
          onClick={incrementQuantity}
          className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
        >
          +
        </button>
      </div>
    </>
  );
});
