import React, {useState} from 'react'

export const Checkbox = React.forwardRef(({ onClick, isSelected, rowData, rowClickCounts, setRowClickCounts, ...rest }, ref) => {
  const handleClick = (event) => {
    setRowClickCounts(prevCounts => {
      const newCounts = { ...prevCounts };
      newCounts[rowData] = (newCounts[rowData] || 0) + 1;
      return newCounts;
    });
    onClick(event);
  };

  const clickCount = rowClickCounts[rowData] || 0;

  return (
    <>
      <button
        ref={ref}
        onClick={handleClick}
        {...rest}
        className="bg-gray-800 hover:bg-gray-500 text-white font-bold py-2 px-4 rounded"
      >
        Добавить в корзину
      </button>
    </>
  );
});
