import React, { useMemo} from 'react'
import { useTable } from 'react-table'
import MOCK_DATA from './MOCK_DATA.json'
import { COLUMNS } from './columns'
export const BasicTable = () => {
    const columns = useMemo(() => COLUMNS, []);
    const data = useMemo(() => MOCK_DATA, []);
    const tableInstance = useTable({
      columns,
      data
    });
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;
  
    return (
      <div className="flex justify-center items-center h-screen bg-gray-300">
        <div className="bg-white rounded-lg w-4/5 h-4/5 p-8 w-full ">
          <div className="w-3/5 mx-auto">
            <table {...getTableProps} className="w-full">
              <thead className="text-black">
                {headerGroups.map((headerGroup, index) => (
                  <tr key={index} {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column, index) => (
                      <th
                        key={index}
                        {...column.getHeaderProps()}
                        className={`px-4 py-2 font-bold bg-yellow-500 text-left ${index === 0 ? 'rounded-l-lg' : ''} ${index === headerGroup.headers.length - 1 ? 'rounded-r-lg' : ''}`}
                      >
                        {column.render('Header')}
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps} className="text-gray-800">
                {rows.map((row, index) => {
                  prepareRow(row);
                  return (
                    <tr key={index} {...row.getRowProps()} className={index % 2 === 1 ? 'bg-gray-200' : 'bg-white'}>
                      {row.cells.map((cell, index) => {
                        return (
                          <td
                            key={index}
                            {...cell.getCellProps()}
                            className={`px-4 py-2 text-left ${index === 0 ? 'rounded-l-lg' : ''} ${index === row.cells.length - 1 ? 'rounded-r-lg' : ''}`}
                          >
                            {cell.render('Cell')}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  };
