import React, { useMemo} from 'react'
import { useTable } from 'react-table'
import MOCK_DATA from './MOCK_DATA.json'
import { COLUMNS } from './columns'
export const BasicTable = () => {
    const columns = useMemo(() => COLUMNS, []);
    const data = useMemo(() => MOCK_DATA, []);
    const tableInstance = useTable({
      columns,
      data,
    });
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
      tableInstance;
  
    return (
      <div className="py-4 px-6 bg-gray-300 rounded-lg overflow-hidden">
        <div className="relative border-8 border-yellow-500 rounded-lg overflow-hidden">
          <div className="absolute inset-0 border-8 border-white rounded-lg overflow-hidden"></div>
          <table
            {...getTableProps}
            className="w-full border-none rounded-lg overflow-hidden"
          >
            <thead className="text-[#444441]">
              {headerGroups.map((headerGroup, index) => (
                <tr key={index} {...headerGroup.getHeaderGroupProps}>
                  {headerGroup.headers.map((column, index) => (
                    <th
                      key={index}
                      {...column.getHeaderProps()}
                      className="px-2 py-2 font-medium text-left border-t-2 border-b-2 border-[#716561]"
                    >
                      {column.render("Header")}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps} className="text-[#444441]">
              {rows.map((row, index) => {
                prepareRow(row);
                return (
                  <tr
                    key={index}
                    {...row.getRowProps()}
                    className={`bg-white ${index % 2 === 0 ? "bg-gray-100" : ""}`}
                  >
                    {row.cells.map((cell, index) => {
                      return (
                        <td
                          key={index}
                          {...cell.getCellProps()}
                          className="px-2 py-2 border-t border-[#716561]"
                        >
                          {cell.render("Cell")}
                        </td>
                      );
                    })}
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  };
