import Header from "./Components/Header";
import React, {useState} from 'react';
import ReactDOM from 'react-dom/client';
import './index.css'
import RegistrationForm from './Components/Register';
import { BasicTable } from './Pages/BasicTable';
import {BrowserRouter as Router,Routes, Route, Link}  from "react-router-dom"
import {Basket} from './Pages/Basket';

function App() {
  const [basketData, setBasketData] = useState([]);
  const handleQuantityChange = (index, value) => {
    if (value === 0) {
      // Удалить строку из basketData
      setBasketData((prevData) =>
        prevData.filter((_, i) => i !== index)
      );
    } else {
      // Обновить количество для строки
      setBasketData((prevData) => {
        const updatedData = [...prevData];
        updatedData[index].clickCount = value;
        return updatedData;
      });
    }
  };

  const decrementQuantity = (index) => {
    const item = basketData[index];
    if (item.clickCount > 0) {
      handleQuantityChange(index, item.clickCount - 1);
    }
  };

  const incrementQuantity = (index) => {
    const item = basketData[index];
    handleQuantityChange(index, item.clickCount + 1);
  };

  return (
    <Router>
      <Header/>
        <Routes>
         <Route
          path="/"
          element={
            <BasicTable basketData={basketData}
              setBasketData={setBasketData}
            />
          }
        />
        <Route
          path="/basket"
          element={
            <Basket basketData={basketData}
             setBasketData={setBasketData}/>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
