import React, {useState} from 'react'

export const Checkbox = React.forwardRef(({ onClick, isSelected, rowData,rowClickCounts, setRowClickCounts, ...rest }, ref) => {
  const [clickCount, setClickCount] = useState(rowClickCounts[rowData.id] || 0);

  const handleClick = (event) => {
    const updatedClickCount = clickCount + 1;
    setClickCount(updatedClickCount);
    setRowClickCounts(prevRowClickCounts => ({
      ...prevRowClickCounts,
      [rowData.id]: updatedClickCount,
    }));
    onClick(event);
  };
 
  
  return (
    <>
      <button
        ref={ref}
        onClick={handleClick}
        {...rest}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Select
      </button>
    </>
  );
});
