import React from 'react'


export const GlobalFilter = ({ filter, setFilter, columns }) => {
  // Находим столбец по наименованию
  const nameColumn = columns.find(column => column.accessor === 'name');

  return (
    <div>
      {/* <input
        value={filter || ''}
        onChange={(e) => setFilter(e.target.value)}
        placeholder="Глобальный поиск"
        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
      /> */}
      {columns
        .filter((column) => column.canFilter)
        .map((column) => (
          <div key={column.id}>
            {column.accessor === 'name' && (
              <div>
                <span>{column.Header}: </span>
                <input
                  value={nameColumn.filterValue || ''}
                  onChange={(e) => nameColumn.setFilter(e.target.value)}
                  placeholder={`Поиск по ${column.Header}`}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
            )}
          </div>
        ))}
    </div>
  );
};
