export const Checkbox = React.forwardRef(({ onClick, isSelected, rowData, rowClickCounts, setRowClickCounts, ...rest }, ref) => {
  const handleClick = (event) => {
    const rowKey = JSON.stringify(rowData); // Преобразуйте rowData в строку для использования в качестве ключа
    setRowClickCounts(prevCounts => ({
      ...prevCounts,
      [rowKey]: (prevCounts[rowKey] || 0) + 1 // Увеличиваем счетчик для этой строки или устанавливаем 1, если его нет
    }));
    onClick(event);
  };

  const rowKey = JSON.stringify(rowData);
  const clickCount = rowClickCounts[rowKey] || 0;

  return (
    <>
      <button
        ref={ref}
        onClick={handleClick}
        {...rest}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Select ({clickCount})
      </button>
    </>
  );
});
