import React from 'react'


export const GlobalFilter = ({ filter, setFilter, columns }) => {
  const nameColumn = columns.find((column) => column.Header === 'Наименование');

  const handleNameFilter = (value) => {
    const newColumns = columns.map((column) => {
      if (column.Header === 'Наименование') {
        return {
          ...column,
          filterValue: value,
          setFilter: (value) => handleNameFilter(value),
        };
      }
      return column;
    });

    setFilter(value);
  };

  return (
    <div>
      <input
        value={filter || ''}
        onChange={(e) => setFilter(e.target.value)}
        placeholder="Глобальный поиск"
        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
      />
      {columns.filter((column) => column.canFilter).map((column) => (
        <div key={column.id} className="mb-3">
          <span>{column.Header}: </span>
          <input
            value={column.filterValue || ''}
            onChange={(d) => column.setFilter(d.target.value)}
            placeholder={`Поиск по ${column.Header}`}
            className="shadow appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
      ))}
      {nameColumn && (
        <div className="mb-3">
          <span>{nameColumn.Header}: </span>
          <input
            value={nameColumn.filterValue || ''}
            onChange={(e) => handleNameFilter(d.target.value)}
            placeholder={`Поиск по ${nameColumn.Header}`}
            className="shadow appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
      )}
    </div>
  );
};
