import React, { Component } from 'react'

// export default class Basket extends Component {
//   render() {
//     return (
//       <div>Basket</div>
//     )
//   }
// }
export const Basket = ({ basketData }) => {
  return (
    <div className="flex flex-col min-h-screen py-8 bg-gray-300">
      <div className="flex-grow bg-white rounded-lg p-8 shadow-lg mx-auto max-w-4xl">
        <h1 className="text-2xl font-bold mb-4">Корзина</h1>
        {basketData.length > 0 ? (
          <table className="table-auto w-full border-collapse">
            <thead>
              <tr>
                <th className="px-4 py-2">Название</th>
                <th className="px-4 py-2">Цена</th>
                <th className="px-4 py-2">Количество</th>
              </tr>
            </thead>
            <tbody>
              {basketData.map((item, index) => (
                <tr key={index} className={index % 2 === 0 ? 'bg-gray-100' : ''}>
                  <td className="px-4 py-2 border">{item.name}</td>
                  <td className="px-4 py-2 border">{item.price}</td>
                  <td className="px-4 py-2 border">
                    <input
                      type="number"
                      value={item.clickCount}
                      min="1"
                      className="w-16 px-2 py-1 border rounded"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Корзина пуста</p>
        )}
      </div>
    </div>
  );
};
