import React from 'react'
// export const Checkbox = React.forwardRef(({ indeterminate, ...rest }, ref) => {
//     const defaultRef = React.useRef()
//     const resolvedRef = ref || defaultRef
  
//     React.useEffect(() => {
//       resolvedRef.current.indeterminate = indeterminate
//     }, [resolvedRef, indeterminate])
  
//     return (
//       <>
//         <input type='checkbox' ref={resolvedRef} {...rest} />
//       </>
//     )
//   })
export const Checkbox = React.forwardRef(({ onClick, row, setSelectedRows, ...rest }, ref) => {
  return (
    <>
      <button
        ref={ref}
        onClick={(event) => {
          event.stopPropagation(); // Останавливаем всплытие события
          onClick(); // Вызываем оригинальный обработчик onClick
          row.toggleRowSelected(true); // Принудительно выбираем строку
          setSelectedRows((prevSelectedRows) => {
            if (prevSelectedRows.includes(row.original)) {
              return prevSelectedRows.filter((r) => r !== row.original);
            } else {
              return [...prevSelectedRows, row.original];
            }
          });
        }}
        {...rest}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        Select
      </button>
    </>
  );
});
