import React, { Component } from 'react'
export const Basket = ({ basketData }) => {
  return (
    <div className="flex flex-col min-h-screen py-8 bg-gray-300">
      <div className="flex-grow bg-white rounded-lg p-8 shadow-lg mx-auto">
        <h1 className="text-2xl font-bold mb-4">Корзина</h1>
        {basketData.length === 0 ? (
          <p>Корзина пуста</p>
        ) : (
          <table className="table-auto w-full border-collapse">
            <tbody>
              {basketData.map((item, index) => (
                <tr key={index} className="border-b">
                  <td className="px-4 py-2">{item.id}</td>
                  <td className="px-4 py-2">{item.name}</td>
                  <td className="px-4 py-2">{item.price}</td>
                  <td className="px-4 py-2">
                    
                    <div className="flex items-center mt-2 max-w-44">
                      <button
                        onClick={decrementQuantity}
                        className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-1 px-2 rounded">
                        -
                      </button>
                        <input
                        type="number"
                        value={item.clickCount}
                        min="0"
                        className="w-20 px-2 py-1 border rounded"
                      />
                      <button
                        onClick={incrementQuantity}
                        className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-1 px-2 rounded">
                        +
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};
